CREATE TABLE BRANDINGS ( brandingid int NOT NULL AUTO_INCREMENT, name varchar(255), latitude double, longitude double, directions varchar(2000), logo_url varchar(255), description varchar(2000), contact_name varchar(255), phone varchar(15), email varchar(255), line1  varchar(255), line2 varchar(255), post_town varchar(255), county varchar(255), post_code varchar(255), primary key (brandingid));